
'use strict';

/**
 * Buzname data object
 */

var BuznameData;

module.exports = BuznameData = function BuznameData(templatename) {
    this.templatename = templatename;
    this.data = {
        pagename : '',
        qualifier : '',
        oldpagename : '',
        pagename2 : '',
        hier1 : '',
        hier2 : '',
        hier3 : '',
        hier4 : '',
        hier5 : '',
        website : '',
        inout : '',
        version : '',
        feature : '',
        subfeature1 : '',
        flowgatename : '',
        flowname : '',
        subfeature2 : '',
        country : '',
        product : ''
    };
};

BuznameData.prototype = {
    set : function (key, value) {
        this.data[key] = value;
    },
    get : function (key) {
        return this.data[key];
    },
    getTemplateName : function () {
        return this.templatename;
    }
};
