'use strict';
process.env.NODE_ENV = 'test';

var assert = require('assert'),
    analytics = require('../');


describe('serverTracking', function () {

    it('no data', function (next) {
        analytics.serverTracking();
        next();
    });

    it('sample data', function (next) {
        analytics.serverTracking({foo: 'bar'}, function (error, response) {
            assert.ok(!error);
            next();
        });
    });

});
