'use strict';

exports = module.exports = {
    /**
     * Encode with PPBase64 character variation.
     * @param data
     * @returns {String}
     */
    base64encode: function b64encode(data) {
        var encoded = new Buffer(data).toString('base64');
        encoded = encoded.replace(/\+/g, '-');
        encoded = encoded.replace(/\//g, '.');
        return encoded;
    },

    /**
     * Decode with PPBase64 character variation.
     * @param data
     * @returns {String}
     */
    base64decode: function b64decode(data) {
        data = data.replace(/\-/g, '+');
        data = data.replace(/\./g, '/');

        return new Buffer(data, 'base64').toString();
    }
};
