'use strict';

module.exports = function LoadByAccountModel(actor) {
    return {
        include_inactive: false,
        account_number: actor.actor_account_number,
        client_actor: actor,
        paging: {
            max_rows: 10,
            start_row: 0
        },
        account_criteria: {
            load_account: true
        },
        alias_criteria: {
            load_aliases: true,
            filter_to_type: 'E',
            primary_only: true
        },
        login_criteria: {
            load_logins: true
        }
    };
};
