'use strict';

var MockSession = require('./mockSession');

function MockRequest() {
    this.session = new MockSession(this);
}

MockRequest.prototype = {
    get: function (name) {
        switch (name) {
            case 'host':
            return 'localhost';
            default:
            return '';
        }
    },
    param: function (name) {
        switch (name) {
            default:
            return '';
        }
    }
};

module.exports = MockRequest;
