'use strict';

var assert = require('chai').assert,
    auth = require('../lib/index');

describe('interface', function () {

    it('should contain api interface', function () {
        assert.strictEqual(typeof auth, 'function');
        assert.strictEqual(typeof auth.authorize, 'function');
        assert.strictEqual(typeof auth.login, 'function');
        assert.strictEqual(typeof auth.logout, 'function');
    });

});