'use strict';

var cal = require('./cal');

module.exports = {

    format: function console(args) {
        var log = cal.format(args);
        log = log.split('\t');
        if (log.length > 3) {
            log = log.slice(3, log.length);
        }
        log = log.join('\t');
        return log;
    }

};
