'use strict';

var CipherSpec = require('./cipherSpec'),
    debug = require('debuglog')('cryptutils-paypal');

module.exports = {

    createCipherSpec: function (name, key, padding, iv) {
        var Ctor;

        Ctor = module.exports[name] || CipherSpec;

        return new Ctor(name, key, padding, iv);
    },

    CipherSpec: CipherSpec,

    desx: require('./desx'),

    aes128: require('./aes128')
};
