'use strict';
/*eslint new-cap:0 */
var Struct = require('struct').Struct;

var headerStruct = Struct()
            .word32Ube('magic')            // Mayfly Magic code
            .word32Ube('msgSize')          // Message Size
            .word32Ube('headerSize')       // Header Size  - 40 bytes
            .word32Ube('operationMsgSize') // Operation Message Size
            .word32Ube('appNameSize')      // Application Name Size
            .word32Ube('senderIP')
            .word32Ube('recipientIP')
            .word16Ube('senderPort')
            .word16Ube('recipientPort')
            .word16Ube('senderType')
            .word16Ube('direction')
            .word32Ube('siteID');

var omHeaderStruct = Struct()
            .word32Ube('recordInfoSize')   // Record Info Size
            .word32Ube('requestIdSize')    // Request ID Size
            .word32Ube('optionalDataSize') // Optional Data Size
            .word32Ube('payloadDataSize')  // Payload Data Size
            .word16Ube('operationType')    // Operation Type
            .word8('operationMode')        // Operation Mode
            .word8('replicateState')       // Replicate State
            .word16Ube('operationStatus')  // Operation Status
            .word16Ube('filler');          // Filler

var requestIdStruct = Struct()
            .word16Ube('rsize')             // request Id size
            .word32Ube('hostip')            // host IP
            .word32Ube('pid')               // process Id
            .word32Sbe('requestingTime')
            .word16Ube('serialNum');        // serial No

var recordInfoHeaderStruct = Struct()
            .word32Sbe('createTime')        // Create Time - 0 in request
            .word32Ube('lifetime')          // LifeTime
            .word16Ube('version')           // Version
            .word16Ube('namespaceSize')     // Namespace Size
            .word16Ube('keySize')           // Namespace Size
            .word16Ube('filler');           // Filler

module.exports = {
    headerStruct: headerStruct,
    omHeaderStruct: omHeaderStruct,
    requestIdStruct: requestIdStruct,
    recordInfoHeaderStruct: recordInfoHeaderStruct
};
