'use strict';

var assert = require('assert');

exports = module.exports = {

    equals: function equals (first, second, len) {
        var i;

        if (!len && first.length !== second.length) {
            return false;
        }

        i = 0;
        len = len || Math.min(first.length, second.length);

        while (i < len) {
            if (first[i] !== second[i]) {
                return false;
            }
            i += 1;
        }

        return true;
    },

    slice: function (obj, start, end) {
        assert(obj && obj.length, 'object has no length.');
        return Array.prototype.slice.call(obj, start, end);
    }

};