'use strict';

module.exports = function (config, transport) {

    return {
        /**
         * callPxp
         * @param pxpQuery
         * @param headers
         * @param callback
         */
        callPxp: function (pxpQuery, headers, callback) {

            transport({
                qs: pxpQuery,
                headers: headers,
                method: 'GET'
            }, callback);
        },

        optInOrOut: function (endpoint, body, headers, callback) {

            transport({
                body: JSON.stringify(body),
                headers: headers,
                method: 'POST',
                path: endpoint
            }, callback);
        }
    };
};