# First make a cdbs directory to appease topos which wants it even in test mode
cp -r ./cdbs ../../

# Then make some ssh keys
openssl genrsa -out ./privatekey.pem 1024;
openssl req -new -newkey rsa:4096 -days 365 -subj '/CN=www.test.com/O=NoCompany/C=US' -nodes -x509  -key ./privatekey.pem  -out ./certrequest.csr;
openssl x509 -x509toreq -days 365 -in ./certrequest.csr -signkey ./privatekey.pem -out ./certificate.req;
openssl x509 -req -in ./certificate.req -signkey ./privatekey.pem -out ./certificate.pem;
