/*global describe, it */

'use strict';

var assert = require('assert'),
    wowo = require('../index');

describe('Test Read Wowo', function () {
    it('Should read wowo from cdb', function (next) {
        assert.ok(wowo.getWowo().isWowoOn('wowo_51564_opf_via_xpt'));
        assert.ok(wowo.getWowo().isWowoOn('wowo_dual_api_credential'));
        assert.ok(wowo.getWowo().isWowoOn('wowo_4515_external_spoof_case_creation'));
        assert.ok(wowo.getWowo().isWowoOff('wowo_28080_tiger_INR'));
        assert.ok(wowo.getWowo().isWowoOff('wowo_28080_allow_INR_for_eup'));
        assert.ok(wowo.getWowo().isWowoOff('wowo_28080_allow_INR_for_ach_out'));
        next();
    });
});
