'use strict';

var assert = require('assert'),
    currencies = require('../lib/constants/currencies'),
    supportedLocales = require('../lib/constants/supported-locales');


describe('constants', function () {

    it('currencies', function (next) {
        assert.ok(currencies.USD.symbol === '$');
        next();
    });

    it('supported locales', function (next) {
        assert.ok(supportedLocales.indexOf('en-US') !== -1);
        next();
    });
});
