
var EventEmitter = require('events').EventEmitter;

function injectRequest() {
    var req = new EventEmitter();
    req.correlationId = 0xabcdef;
    req.headers = {};
    req.query = {};
    req.url = '/';
    req.header = function () {};
    return req;
}

function injectResponse(req) {
    var res = new EventEmitter();
    res.req = req || {};
    res.header = function () {};
    res.cookie = function () {};
    res.locals = {
        context: {links: {templateBaseUrl: 'https://www.paypalobjects.com/web/res/abc/def789321'}}
    };
    return res;
}

module.exports = {
    injectRequest: injectRequest,
    injectResponse: injectResponse
};