'use strict';

var assert = require('assert');
var getInstanceId = require('../index').getInstanceId;
var res;

describe('index', function () {

	it('simple non-clustered test', function (next) {
		res = getInstanceId();
		assert.equal(res,  1);
		next();
	});
	it('simple ebay phx test', function (next) {
    		process.env.pm_id = 1;
		res = getInstanceId();
		assert.equal(res, 2);
		next();
	});

});
