var assert = require("assert");
var SignedInt32 = require("../lib/signedint/SignedInt32");

describe('SignedInt32', function () {
    describe('#inc() of INT_MAX', function () {
        it('should return INT_MIN', function () {
            assert.equal(SignedInt32.INT_MIN, new SignedInt32(SignedInt32.INT_MAX).inc());
        });
    });

    describe('#dec() of INT_MIN', function () {
        it('should return INT_MAX', function () {
            assert.equal(SignedInt32.INT_MAX, new SignedInt32(SignedInt32.INT_MIN).dec());
        });
    });
});