'use strict';

var base64 = require('./index');

function Base64 (options) {
    this.charSet = options ? options.characterSet : undefined;
    this.pad = options ? !options.noPadding : undefined;
}

Base64.CharacterSet = base64.CharacterSet;

Base64.prototype = {

    encode: function (input) {
        return base64.encode(input, this.charSet, this.pad);
    },

    decode: function (input) {
        return base64.decode(input, this.charSet, this.pad);
    }

};

module.exports = Base64;