'use strict';

var CipherSpec = require('./cipherSpec');

module.exports = {

    createCipherSpec: function (name, key, padding, iv) {
        var SpecCtor;

        try {
            SpecCtor = require('./' + name);
        }
        catch (e) {
            return new CipherSpec(name, key, padding, iv);
        }

        return new SpecCtor(name, key, padding);
    },

    CipherSpec: CipherSpec

};
