/*global describe:false, it:false*/
'use strict';

var base64 = require('../lib/base64'),
    assert = require('chai').assert;

describe('Base64', function () {

    it('should encode default character set', function () {
        var b64str = base64.encode('Hello World!').toString();
        assert.strictEqual(b64str, new Buffer('Hello World!').toString('base64'));
    });

    it('should decode default character set', function () {
        var original = base64.decode('SGVsbG8gV29ybGQh').toString();
        assert.strictEqual(original, 'Hello World!');

    });

});