'use strict';

var deployEnv = require('environment-paypal'),
    remotopo = require('./lib/remotopo.js');

// && !process.env.BUILD_ID - removing for now.
if (deployEnv.isDev()) {

    remotopo.load(function (err) {
        if (err) {
            console.error('*** Error loading topos info from github for dev use: %s', err.message);
        }

        process.exit(0);
    });

} else {
    console.log('topos: skipping dev postinstall step');
}
