#!/usr/bin/env node
'use strict';

var path = require('path'),
    root = process.cwd(),
    pkg = require(path.join(root, 'package')),
    serviceConf = require('../lib/service-configurator');

//This is a CLI Invocation. Fine to use the try/catch and return exit code.
try {
    serviceConf(pkg.name);
    process.exit(0);
} catch (err) {
    console.log(err);
    process.exit(1);
}

