/*global describe: false, it: false, before: false, after: false*/
'use strict';


var apputil = require('../lib/util/apputil'),
    assert = require('chai').assert;


describe('apputil', function () {

    describe('scanDependencies', function () {

        it('should invoke the provided handler on each module', function (next) {
            var deps = [];

            apputil.scanDependencies(process.cwd(), true, function (pkg) {
                deps.push(pkg.name);
            });


            assert.ok(deps.length > 0);
            assert.ok(~deps.indexOf('topos')); // Includes self/root
            next();
        });

    });

});