'use strict';

var keywrap = require('./keywrap');


var DEFAULT_IV =  new Buffer([ 0xA6, 0xA6, 0xA6, 0xA6, 0xA6, 0xA6, 0xA6, 0xA6 ]);

exports = module.exports = {

    wrap: function (input, kek) {
        return keywrap.wrap(input, kek, DEFAULT_IV);
    },

    unwrap: function (input, kek) {
        return keywrap.unwrap(input, kek, DEFAULT_IV);
    }

};