'use strict';

var CdbReader = require('./lib/cdb'),
    CdbSync = require('./lib/sync'),
    StreamReader = require('./lib/stream');

module.exports = {

    createReader: function (path) {
        return new CdbReader(path);
    },


    createReadStream: function (file) {
        return new StreamReader(file);
    },


    readSync: function (path) {
        return CdbSync.readSync(path);
    }

};