var assert = require('assert'),
    FISourceGenerator = require('./test-utils/fiSourceGenerator');

describe('FISourceGenerator Tests', function () {
    var fiSourceGenerator = new FISourceGenerator();
    
    it('should test generating a card number for VISA', function (done) {
        fiSourceGenerator.cardGenerate('VISA', function (err, result) {
            assert.ok(!!result);
            assert.ok(typeof +result === 'number');
            done();
        });
    });

    it('should test generating a card number for switch', function (done) {
        fiSourceGenerator.cardGenerate('switch', function (err, result) {
            assert.ok(!!result);
            assert.ok(typeof +result === 'number');
            done();
        });
    });

    it('should test generating a bank number for US', function (done) {
        fiSourceGenerator.bankGenerate('Checking', function (err, result) {
            assert.ok(!!result);
            assert.ok(!!result.bankName);
            assert.ok(!!result.accountNum);
            done();
        });
    });
});